import React from 'react';
import {
  Typography,
  Box,
  Container,
  Paper,
  Grid,
  Avatar,
  TextField,
  Button
} from '@mui/material';
import { Edit } from '@mui/icons-material';

const Profilim = ({ userProfile }) => {
  if (!userProfile) {
    return (
      <Container maxWidth="lg" sx={{ mt: 4, pb: 4 }}>
        <Typography variant="h4">Profil bilgileri yÃ¼kleniyor...</Typography>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4, pb: 4, position: 'relative' }}>
      {/* Page Title */}
      <Typography variant="h4" sx={{ fontWeight: 'bold', color: '#1a237e', mb: 4 }}>
        ðŸ‘¤ Profilim
      </Typography>

      {/* Main two-column layout */}
      <Grid container spacing={4}>
        {/* Left Profile Section (narrower) */}
        <Grid item xs={12} md={4}>
          <Paper 
            elevation={3} 
            sx={{ 
              p: 3, 
              textAlign: 'center',
              height: '100%',
              display: 'flex',
              flexDirection: 'column'
            }}
          >
            {/* Avatar with user initial */}
            <Avatar
              src={userProfile.profilePhoto}
              alt={userProfile.name}
              sx={{ 
                width: 120, 
                height: 120, 
                mx: 'auto', 
                mb: 2,
                fontSize: '3rem',
                bgcolor: '#1a237e'
              }}
            >
              {userProfile.name ? userProfile.name.charAt(0).toUpperCase() : 'A'}
            </Avatar>
          </Paper>
        </Grid>

        {/* Right Profile Section (wider) */}
        <Grid item xs={12} md={8}>
          <Paper 
            elevation={3} 
            sx={{ 
              p: 3,
              position: 'relative',
              height: '100%',
              display: 'flex',
              flexDirection: 'column'
            }}
          >
            {/* "Only visible on this screen" note will be added in Task 3.1 */}
            
            {/* Content will be implemented in Task 3 */}
            <Typography variant="body2" color="text.secondary">
              Right Profile Section (To be implemented)
            </Typography>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Profilim;
